example.code:
/* CSS Syntax */
selector {
  property: value;
}

/* Example */
p {
  color: blue;
  font-size: 16px;
  margin: 10px;
}

/* Element selector */
h1 {
  color: navy;
}

/* Class selector */
.highlight {
  background-color: yellow;
}

/* ID selector */
#main-title {
  font-size: 32px;
}

/* Combining selectors */
p.intro {
  font-weight: bold;
}

.box {
  width: 200px;
  height: 100px;
  padding: 20px;
  border: 2px solid black;
  margin: 10px;
}
